import React,{useState, useEffect} from 'react';

const CustomerControl = ()=>{
    const [AllCustomers,setCustomers] = useState([]);
    const [name,setName] = useState('');
    const [phone,setPhone] = useState('');
    const [email,setEmail] = useState('');
    const [house_no,setHouse] = useState('');
    const [city,setCity] = useState('');
    const [zipcode,setZipCode] = useState('');
    const [username,setUsername] = useState('');
    const [password,setPassword] = useState('');

    const GetCustomer = async()=>{
        try {
            const parameters = window.location.search.substring(1).split("&");
            const temp = parameters[0].split("=");
            console.log(parameters);
            console.log(temp);
            const username = temp[1];
            const query = await fetch(`http://localhost:5000/customer/${username}`);
            console.log(query.json());
        } catch (error) {
            console.log(error);
        }
    };
    useEffect(()=>{
        GetCustomer();
    },[]);
    return (
<div>
    <h1 className='mt-5' style={{textAlign : 'center'}}>Customer Details</h1>
<div className='container border mt-5 p-5'>
<div className="input-group mb-3">
  <div className="input-group-prepend">
    <span className="input-group-text" id="basic-addon1">customerid@</span>
  </div>
  <input type="text" className="form-control" placeholder="id" aria-label="Username" aria-describedby="basic-addon1" readOnly/>
</div>
<div className="input-group mb-3">
  <div className="input-group-prepend">
    <span className="input-group-text" id="basic-addon1">customername@</span>
  </div>
  <input type="text" className="form-control" placeholder="Name" aria-label="Username" aria-describedby="basic-addon1" readOnly/>
</div>
<div className="input-group mb-3">
  <div className="input-group-prepend">
    <span className="input-group-text" id="basic-addon1">phone@</span>
  </div>
  <input type="text" className="form-control" placeholder="id" aria-label="Username" aria-describedby="basic-addon1" readOnly/>
</div>
<div className="input-group mb-3">
  <div className="input-group-prepend">
    <span className="input-group-text" id="basic-addon1">email@</span>
  </div>
  <input type="text" className="form-control" placeholder="Name" aria-label="Username" aria-describedby="basic-addon1" readOnly/>
</div>
<div className="input-group mb-3">
  <div className="input-group-prepend">
    <span className="input-group-text" id="basic-addon1">house@</span>
  </div>
  <input type="text" className="form-control" placeholder="id" aria-label="Username" aria-describedby="basic-addon1" readOnly/>
</div>
<div className="input-group mb-3">
  <div className="input-group-prepend">
    <span className="input-group-text" id="basic-addon1">city@</span>
  </div>
  <input type="text" className="form-control" placeholder="Name" aria-label="Username" aria-describedby="basic-addon1" readOnly/>
</div>
<div className="input-group mb-3">
  <div className="input-group-prepend">
    <span className="input-group-text" id="basic-addon1">zipcode@</span>
  </div>
  <input type="text" className="form-control" placeholder="Name" aria-label="Username" aria-describedby="basic-addon1" readOnly/>
</div>

<hr className='mt-5'></hr>
<label>Your vanity URL</label>
<div className="input-group mb-3">
  <div className="input-group-prepend">
    <span className="input-group-text" id="basic-addon3">@username</span>
  </div>
  <input type="text" className="form-control" id="basic-url" aria-describedby="basic-addon3" readOnly/>
</div>

<div className="input-group mb-3">
  <div className="input-group-prepend">
    <span className="input-group-text">$</span>
  </div>
  <input type="text" className="form-control" aria-label="Amount (to the nearest dollar)" readOnly/>
  <div className="input-group-append">
    <span className="input-group-text">.00</span>
  </div>
</div>

</div>
</div>
    );
};

export default CustomerControl;
